// Write your code here
import {Component} from 'react'

import VaccinationByAge from '../VaccinationByAge'

import VaccinationByGender from '../VaccinationByGender'

import VaccinationCoverage from '../VaccinationCoverage'

class CowinDashboard extends Component {
  state = {last7Days: [], age: [], gender: []}

  componentDidMount = () => {
    this.apiRequest()
  }

  apiRequest = async () => {
    const dd = await fetch('https://apis.ccbp.in/covid-vaccination-data')
    const response = await dd.json()
    console.log(response)
    const updated7DaysList = response.last_7_days_vaccination.map(each => ({
      dose1: each.dose_1,
      dose2: each.dose_2,
      vaccineDate: each.vaccine_date,
    }))

    this.setState({
      last7Days: updated7DaysList,
      age: response.vaccination_by_age,
      gender: response.vaccination_by_gender,
    })
  }

  render() {
    const {last7Days, gender, age} = this.state
    return (
      <div className="bg">
        <img
          src="https://assets.ccbp.in/frontend/react-js/cowin-logo.png"
          alt="website logo"
        />
        <h1>CoWIN Vaccination in India</h1>

        <div>
          <VaccinationByAge last7Days={last7Days} />
        </div>
        <div>
          <VaccinationByGender gender={gender} />
        </div>
        <div>
          <VaccinationCoverage age={age} />
        </div>
      </div>
    )
  }
}

export default CowinDashboard
